import React, { Component } from "react";
import "./acquisition.css";
import Aux from "../../../hoc/Aux";
// import List from "../../../ui/list/list";
import Acquisitions from "./acquisitions";
import Acquire from "./acquire";
import axios from "../../../axios-scrape";
class Acquisition extends Component {
  state = {
    acquisitions: [],
    books: [],
    searchTerm: "",
    toAcquisitions: true,
    toAcquire: false,
    error: null,
  };

  togglePages = () => {
    this.setState((prevState) => {
      return {
        toAcquisitions: !prevState.toAcquisitions,
        toAcquire: !prevState.toAcquire,
      };
    });
  };

  scrapeBooks = () => {
    axios.get(`?q=${this.state.searchString}`).then((res) => {
      console.log(res);
    });
  };

  handleSearch = (e) => {
    e.preventDefault();
    this.setState({ searchTerm: e.target.value });
    console.log(e.target.value);
  };

  render() {
    // let acquisitionsClasses = ["acquisitions", "open-acquisitions"];
    // let acquireClasses = ["acquire", "close-acquire"];
    // if (this.state.toAcquire) {
    //   acquisitionsClasses = ["acquisitions", "close-acquisitions"];
    //   acquireClasses = ["acquire", "open-acquire"];
    // }
    const attributes = [
      "Date Of Acquisition",
      "Number Of Books Acquired",
      "Budget Cost",
    ];

    let page = (
      <Acquisitions
        attributes={attributes}
        acquisitions={this.state.acquisitions}
        togglePages={this.togglePages}
      />
    );
    if (this.state.toAcquire) {
      page = (
        <Acquire
          togglePages={this.togglePages}
          handleSearch={this.handleSearch}
          scrapeBooks={this.scrapeBooks}
          value={this.state.searchTerm}
        />
      );
    }
    return <Aux>{page}</Aux>;
  }
}
export default Acquisition;
