import React, { Component } from "react";
import "./acquisition.css";
import Aux from "../../../hoc/Aux";
// import List from "../../../ui/list/list";
import Acquisitions from "./acquisitions";
import Acquire from "./acquire/acquire";
import axios from "../../../axios-scrape";
import { Route, Switch } from "react-router-dom";
import { FaSignOutAlt } from "react-icons/fa";

class Acquisition extends Component {
  state = {
    acquisitions: [],
    books: [],
    searchTerm: "",
    toAcquisitions: true,
    toAcquire: false,
    error: null,
  };

  componentDidMount = () => {};
  togglePages = () => {
    // console.log("hello");
    // this.setState((prevState) => {
    //   return {
    //     toAcquisitions: !prevState.toAcquisitions,
    //     toAcquire: !prevState.toAcquire,
    //   };
    // });
    this.props.history.push(this.props.match.path + "/acquire");
  };

  scrapeBooks = () => {
    axios
      .get(`?q=${this.state.searchTerm}&maxResults=40&startIndex=0`)
      .then((res) => {
        console.log(res);
        this.setState({ books: this.cleanData(res.data.items) });
      })
      .catch((error) => this.setState({ error }));

    const queryString =
      encodeURIComponent("search_query") +
      "=" +
      encodeURIComponent(this.state.searchTerm);
    this.props.history.push({
      pathname: this.props.match.path + "/acquire/results",
      search: "?" + queryString,
    });
  };

  handleSearch = (e) => {
    // e.preventDefault();
    // this.setState({ searchTerm: e.target.value });
    this.props.push();
  };

  cleanData = (books) => {
    const cleanedDate = books.map((book) => {
      if (!book.volumeInfo.hasOwnProperty("imageLinks")) {
        book.volumeInfo["imageLinks"] = {
          thumbnail:
            "https://vignette.wikia.nocookie.net/pandorahearts/images/a/ad/Not_available.jpg/revision/latest?cb=20141028171337",
        };
      }
      if (!book.volumeInfo.hasOwnProperty("authors")) {
        book.volumeInfo["authors"] = [];
      }
      return book;
    });

    return cleanedDate;
  };

  render() {
    const attributes = [
      "Date Of Acquisition",
      "Number Of Books Acquired",
      "Budget Cost",
    ];
    return (
      <Aux>
        <Switch>
          <Route
            path={this.props.match.path + "/acquire"}
            render={() => (
              <Acquire
                togglePages={this.togglePages}
                handleSearch={this.handleSearch}
                scrapeBooks={this.scrapeBooks}
                value={this.state.searchTerm}
                books={this.state.books}
              />
            )}
          />
          <Route
            path={this.props.match.path}
            render={() => (
              <Acquisitions
                attributes={attributes}
                acquisitions={this.state.acquisitions}
                togglePages={this.togglePages}
              />
            )}
          />
        </Switch>
      </Aux>
    );
  }
}
export default Acquisition;
