import React, { useState, useEffect } from "react";
import styles from "./SearchResult.module.css";
import StarRating from "../../../ui/star/StarRating";
import Spinner from "../../../ui/loader/Spinner/Spinner";
import Aux from "../../../hoc/Aux";
import axios from "../../../axios-scrape";
import ScrapeLoader from "../../../ui/loader/ScrapeLoader/ScrapeLoader";
import { Link } from "react-router-dom";

const SearchResult = (props) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [pagenatedLoading, setPagenatedLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    window.addEventListener("scroll", performScrollEvent);
    const query = new URLSearchParams(props.location.search);
    let search = "";
    for (let param of query.entries()) {
      search = param[1];
    }
    loadData(search);
    const performScrollEvent = () => {
      const scrollable =
        document.documentElement.scrollHeight - window.innerHeight;
      const scrolled = window.scrollY;
      if (Math.ceil(scrolled) === scrollable) {
        getMoreBooks(books.length + 1);
      }
    };
    return () => {
      window.removeEventListener("scroll", performScrollEvent);
    };
  }, [books, props.location.search]);
  // state = {
  //   books: [],
  //   loading: false,
  //   pagenatedLoading: false,
  //   error: null,
  //   searchTerm: "",
  // };

  function loadData(search) {
    if (search) {
      if (!books || search !== searchTerm) {
        console.log("making request");
        // this.setState({ loading: true, searchTerm: search, books: [] });
        setLoading(true);
        setSearchTerm(search);
        setBooks([]);
        axios
          .get(`?q=${search}&maxResults=10&startIndex=0`)
          .then((res) => {
            console.log(res);
            // this.setState({
            //   books: this.cleanData(res.data.items),
            //   loading: false,
            // });
            setBooks(cleanData(res.data.items));
            setLoading(false);
          })
          .catch((error) => {
            setLoading(false);
            setError(error);
          });
      }
    }
    console.log(error);
  }

  const cleanData = (books) => {
    const cleanedDate = books.map((book) => {
      if (!book.volumeInfo.hasOwnProperty("imageLinks")) {
        book.volumeInfo["imageLinks"] = {
          thumbnail:
            "https://vignette.wikia.nocookie.net/pandorahearts/images/a/ad/Not_available.jpg/revision/latest?cb=20141028171337",
        };
      }
      if (!book.volumeInfo.hasOwnProperty("authors")) {
        book.volumeInfo["authors"] = [];
      }
      return book;
    });
    return cleanedDate;
  };

  const getMoreBooks = (startIndex) => {
    // this.setState({ pagenatedLoading: true });
    setPagenatedLoading(true);
    axios
      .get(`?q=${searchTerm}&maxResults=10&startIndex=${startIndex}`)
      .then((res) => {
        // this.setState({
        //   books: [...this.state.books.concat(this.cleanData(res.data.items))],
        //   pagenatedLoading: false,
        // });
        setBooks([...books.concat(cleanData(res.data.items))]);
      })
      .catch((error) => {
        setError(error);
        setPagenatedLoading(false);
      });
  };

  // componentDidMount() {
  //   window.addEventListener("scroll", this.performScrollEvent);
  //   this.loadData();
  // }

  // componentDidUpdate() {
  //   this.loadData();
  // }

  // componentWillUnmount() {
  //   window.removeEventListener("scroll", this.performScrollEvent);
  // }

  let results = loading ? <ScrapeLoader /> : null;
  if (books.length > 0) {
    console.log("yes");
    results = (
      <div>
        <ul className={styles.BookList}>
          {books.map((book) => {
            return (
              <Link to={"/acquire_new/" + book.id}>
                <li className={styles.BookItem} key={book.id}>
                  <div className={styles.ThumbnailSection}>
                    <img
                      src={book.volumeInfo.imageLinks.thumbnail}
                      alt="book"
                    />
                  </div>
                  <div className={styles.BookSummary}>
                    <h3>{book.volumeInfo.title}</h3>
                    <h4>
                      <span>by </span>
                      {book.volumeInfo.authors.join(", ")}
                    </h4>
                    <StarRating rate={book.volumeInfo.averageRating} />
                  </div>
                </li>
              </Link>
            );
          })}
        </ul>
        {pagenatedLoading ? <Spinner /> : null}
      </div>
    );
  }
  return <Aux> {results} </Aux>;
};

export default SearchResult;
