import React from "react";

const button = (props) => {
  const btnStyle = {
    btn: {
      backgroundColor: "transparent",
      border: "none",
      borderRadius: "100px",
      color: "#fff",
      padding: "0.4rem 1rem",
      marginLeft: "0.7rem",
      cursor: "pointer",
      font: "inherit",
      outline: "none",
      fontWeight: "bold",
    },
  };
  return (
    <button
      className={[styles.btn, props.btnType].join(" ")}
      onClick={props.clicked}
      style={({ ...btnStyle.btn }, { ...props.style })}
    >
      {props.children}
    </button>
  );
};
export default button;
