import React, { Component } from "react";
import "./acquisition.css";
import Aux from "../../../hoc/Aux";
// import List from "../../../ui/list/list";
import Acquisitions from "./acquisitions";
import Acquire from "./acquire/Acquire";

class Acquisition extends Component {
  state = {
    acquisitions: [],
    books: [],
    searchTerm: "",
    toAcquisitions: true,
    toAcquire: false,
    error: null,
  };
  componentDidMount = () => {
    if (sessionStorage.getItem("toAcquisition")) {
      console.log("yess");
    }
  };
  togglePages = () => {
    console.log("hello");
    this.setState((prevState) => {
      return {
        toAcquisitions: !prevState.toAcquisitions,
        toAcquire: !prevState.toAcquire,
      };
    });
  };

  render() {
    const attributes = [
      "Date Of Acquisition",
      "Number Of Books Acquired",
      "Budget Cost",
    ];

    let pages = (
      <Acquisitions
        attributes={attributes}
        acquisitions={this.state.acquisitions}
        togglePages={this.togglePages}
      />
    );

    if (this.state.toAcquire) {
      pages = <Acquire togglePages={this.togglePages} />;
    }
    return <Aux>{pages}</Aux>;
  }
}
export default Acquisition;
