import React from "react";
import "./Chart.scss";

import { Line, Bar, Doughnut } from "react-chartjs-2";
import Aux from "../../hoc/Aux";

const chart = (props) => {
  const data = {
    labels: [
      "10/04/2018",
      "10/05/2018",
      "10/06/2018",
      "10/07/2018",
      "10/08/2018",
      "10/09/2018",
      "10/10/2018",
      "10/11/2018",
      "10/12/2018",
      "10/13/2018",
      "10/14/2018",
      "10/15/2018",
    ],
    datasets: [
      {
        label: "Temperature",
        data: [22, 19, 27, 23, 22, 24, 17, 25, 23, 24, 20, 19],
        fill: false, // Don't fill area under the line
        borderColor: "green", // Line color
      },
    ],
  };

  const doughnutData = {
    labels: ["Satisfied", "Partially Satisfied", "Not Satisfied"],
    datasets: [
      {
        label: "points",
        data: [23, 52, 25],
        backgroundColor: ["#fff", "#51087e", "#2980b9"],
      },
    ],
  };

  const option = {
    maintainAspectRatio: false,
    legend: {
      display: true,
      position: "right",
    },
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  };

  const doughnutOption = {
    maintainAspectRatio: false,
    legend: {
      display: true,
      position: "right",
    },
    // scales: {
    //   yAxes: [
    //     {
    //       ticks: {
    //         beginAtZero: true,
    //       },
    //     },
    //   ],
    // },
  };

  return (
    <div className="charts">
      <div className="line">
        <Line data={data} options={option} />
      </div>
      <div className="bar">
        <Doughnut data={doughnutData} options={doughnutOption} />
      </div>
    </div>
  );
};
export default chart;
