import React, { Component } from "react";
// import logo from "./logo.svg";
import "./App.css";

import Person from "./component/Person";

class App extends Component {
  state = {
    persons: [
      { name: "Denzel", age: 23 },
      { name: "Queen", age: 21 },
      { name: "Manu", age: 24 },
    ],

    showPerson: false,
  };

  changeHandler = (ev, person) => {
    // const persons = this.state.persons.splice();
    const persons = [...this.state.persons];
    const index = persons.indexOf(person);
    const newperson = persons[index];
    newperson.name = ev.target.value;
    this.setState({ persons });
  };

  removePersonHandler = (index) => {
    const persons = this.state.persons.slice();
    persons.splice(index, 1);
    this.setState({ persons: persons });
  };

  togglePerson = () => {
    this.setState({ showPerson: !this.state.showPerson });
  };

  render() {
    const style = {
      backgroundColor: "green",
      color: "#fff",
    };

    let persons = <p>No Persons</p>;
    if (this.state.showPerson) {
      persons = (
        <div>
          {this.state.persons.map((person, i) => (
            <Person
              key={i}
              person={person}
              changeHandler={this.changeHandler}
              click={() => this.removePersonHandler(i)}
            />
          ))}
        </div>
      );

      style.backgroundColor = "red";
    }
    let classes = [];
    if (this.state.persons.lenght <= 2) {
      console.log("red");
      classes.push("red");
    }

    if (this.state.persons.length <= 1) {
      classes.push("bold");
    }

    return (
      <div className="App">
        <h1>Welcome React</h1>
        <p className={classes.join(" ")}>
          There are {this.state.persons.length} persons left
        </p>
        <button style={style} onClick={this.togglePerson}>
          Toggle Person
        </button>
        {persons}
      </div>
    );
  }
}

export default App;
