import React, { Component } from "react";
import Aux from "../../../hoc/Aux";
// import List from "../../../ui/list/list";
import Acquisitions from "./acquisitions";
import Button from "../../../ui/button/button";
import "./acquisition.css";
class Acquisition extends Component {
  state = {
    acquisitions: [],
    closeAcquisition: false,
  };

  closeAquisition = () => {
    this.setState({ closeAcquisition: true });
  };

  render() {
    let attachedClasses = ["acquisitions", "open"];
    if (this.state.closeAcquisition) {
      attachedClasses = ["acquisitions", "close"];
    }
    const attributes = [
      "Date Of Acquisition",
      "Number Of Books Acquired",
      "Budget Cost",
    ];
    return (
      <Aux>
        <Button
          btnType="primary"
          style={{ float: "right" }}
          clicked={this.closeAcquisition}
        >
          Go Acquire
        </Button>
        <div className="clr" style={{ marginBottom: "0.75rem" }}></div>
        <div className={attachedClasses.join(" ")}>
          <Acquisitions
            attributes={attributes}
            acquisitions={this.state.acquisitions}
          />
        </div>
      </Aux>
    );
  }
}
export default Acquisition;
