import React, { Component } from "react";
import styles from "./SearchResult.module.css";
import StarRating from "../../../ui/star/StarRating";
import Spinner from "../../../ui/loader/Spinner/Spinner";
import Aux from "../../../hoc/Aux";
import ScrapeLoader from "../../../ui/loader/ScrapeLoader/ScrapeLoader";

class SearchResult extends Component {
  state = {
    book: [],
    loading: false,
    error: null,
  };

  componentDidMount() {
    window.addEventListener("scroll", this.performScrollEvent);
    this.setState({ books: [] });
    this.setState({ loading: true });
    axios
      .get(`?q=${this.state.searchTerm}&maxResults=10&startIndex=0`)
      .then((res) => {
        console.log(res);
        this.setState({
          books: this.cleanData(res.data.items),
          loading: false,
        });
      })
      .catch((error) => this.setState({ error, loading: false }));
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.performScrollEvent);
  }

  getMoreBooks = (startIndex) => {
    this.setState({ loading: true });
    axios
      .get(`?q=${this.state.searchTerm}&maxResults=10&startIndex=${startIndex}`)
      .then((res) => {
        console.log(res);
        this.setState({
          books: [...this.state.books.concat(this.cleanData(res.data.items))],
          loading: false,
        });
      })
      .catch((error) => this.setState({ error, loading: false }));
  };

  cleanData = (books) => {
    const cleanedDate = books.map((book) => {
      if (!book.volumeInfo.hasOwnProperty("imageLinks")) {
        book.volumeInfo["imageLinks"] = {
          thumbnail:
            "https://vignette.wikia.nocookie.net/pandorahearts/images/a/ad/Not_available.jpg/revision/latest?cb=20141028171337",
        };
      }
      if (!book.volumeInfo.hasOwnProperty("authors")) {
        book.volumeInfo["authors"] = [];
      }
      return book;
    });
    return cleanedDate;
  };

  performScrollEvent = () => {
    const scrollable =
      document.documentElement.scrollHeight - window.innerHeight;
    const scrolled = window.scrollY;
    if (Math.ceil(scrolled) === scrollable) {
      this.moreBooks(this.state.books.length + 1);
    }
  };

  render() {
    let results = this.state.loading ? <ScrapeLoader /> : null;
    if (this.state.books > 0) {
      results = (
        <Aux>
          <ul className={styles.BookList}>
            {this.props.books.map((book) => {
              return (
                <li className={styles.BookItem} key={book.id}>
                  <div className={styles.ThumbnailSection}>
                    <img
                      src={book.volumeInfo.imageLinks.thumbnail}
                      alt="book"
                    />
                  </div>
                  <div className={styles.BookSummary}>
                    <h3>{book.volumeInfo.title}</h3>
                    <h4>
                      <span>by </span>
                      {book.volumeInfo.authors.join(", ")}
                    </h4>
                    <StarRating rate={book.volumeInfo.averageRating} />
                  </div>
                </li>
              );
            })}
          </ul>
          {this.props.loading ? <Spinner /> : null}
        </Aux>
      );
    }
    return { results };
  }
}

export default SearchResult;
