import React, { Component } from "react";
import Aux from "../../hoc/Aux";

import NavigationItems from "../../components/Navigation/NavigationItems/NavigationItems";
import Cards from "../../ui/card/Cards";
import Table from "../../ui/table/Table";

class CollectionBuilder extends Component {
  state = {
    collections: [1, 2, 3],
    materialsBorrowed: [],
    frequentlyUsed: [],
    infrequentlyUsed: [],
    renderedMaterials: [],
    attributes: [],
    showTable: false,
    tableTitle: "",
  };

  componentDidMount() {
    console.log("In CollectionBuilder");
  }

  getAllCollections = () => {
    const attributes = [
      "Title",
      "Author",
      "Pagination",
      "PrintType",
      "Acquisition Date",
      "ISBN-13",
    ];
    this.setState({
      attributes,
      showTable: true,
      tableTitle: "Total Collections",
    });
  };

  getBorrowedMaterials = () => {
    const attributes = [
      "Title",
      "Author",
      "User",
      "PrintType",
      "Borrowed Date",
      "Due Date",
      "ISBN-13",
    ];
    this.setState({ attributes, showTable: true });
  };

  getFrequentAskedMaterials = () => {
    const attributes = [
      "Title",
      "Author",
      "ISBN-13",
      "Pagination",
      "Number of Times Requested",
      "Available",
      "Date Requested",
    ];
    this.setState({ attributes, showTable: true });
  };

  render() {
    const links = ["collection", "acquisition"];
    const collectionsInfo = [
      {
        key: "collections",
        label: "Total Collections",
        value: 0,
      },
      {
        key: "materialsBorrowed",
        label: "Materials Borrowed",
        value: 0,
      },
      {
        key: "frequentlyUsed",
        label: "Frequently Used",
        value: 0,
      },
      {
        key: "infrequentlyUsed",
        label: "Infrequently Used",
        value: 0,
      },
    ];
    return (
      <Aux>
        <header>
          <nav>
            <NavigationItems items={links} />
          </nav>
        </header>
        <div className="container">
          <div className="title-area">
            <h4 className="title">Collections</h4>
          </div>
          <Cards items={collectionsInfo} clicked={this.getAllCollections} />
          {this.state.showTable ? (
            <Table
              title={this.state.tableTitle}
              headers={this.state.attributes}
              data={this.state.collections}
            />
          ) : null}
        </div>
      </Aux>
    );
  }
}
export default CollectionBuilder;
