import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

import Person from "./component/Person";

class App extends Component {
  state = {
    persons: [
      { name: "Denzel", age: 23 },
      { name: "Queen", age: 21 },
      { name: "Manu", age: 24 },
    ],
  };

  changeHandler = (person) => {
    console.log(this.state.persons.indexOf(person));
  };
  render() {
    return (
      <div className="App">
        <h1>Welcome React</h1>

        {this.state.persons.map((person) => (
          <Person
            name={person}
            age={person}
            changeHandler={this.changeHandler}
          />
        ))}
      </div>
    );
  }
}

export default App;
