import React, { Component } from "react";
import Aux from "../../hoc/Aux";

import NavigationItems from "../../components/Navigation/NavigationItems/NavigationItems";
import Cards from "../../ui/card/Cards";
import Table from "../../ui/table/Table";

class Collection extends Component {
  state = {
    collections: [1, 2, 3],
    materialsBorrowed: [],
    frequentlyUsed: [],
    infrequentlyUsed: [],
    renderedMaterials: [],
  };

  renderMaterials = (key) => {
    const materials = [...this.state[key]];
    this.setState({ renderedMaterials: materials });
  };

  render() {
    const links = ["collection", "acquisition"];
    const collectionsInfo = [
      {
        key: "collections",
        label: "Total Collections",
        value: 0,
      },
      {
        key: "materialsBorrowed",
        label: "Materials Borrowed",
        value: 0,
      },
      {
        key: "frequentlyUsed",
        label: "Frequently Used",
        value: 0,
      },
      {
        key: "infrequentlyUsed",
        label: "Infrequently Used",
        value: 0,
      },
    ];
    return (
      <Aux>
        <header>
          <nav>
            <NavigationItems items={links} />
          </nav>
          <div className="title-area">
            <h4 className="title">Collections</h4>
          </div>
        </header>
        <div className="main-contents container">
          <Cards items={collectionsInfo} clicked={this.renderMaterials} />
          <Table data={this.state.renderedMaterials} />
        </div>
      </Aux>
    );
  }
}
export default Collection;
