import React, { useState } from "react";
import "./acquire.css";
import Button from "../../../../ui/button/button";
import SearchResult from "./searchresult";
import ScrapeLoader from "../../../../ui/loader/scrape-loader";

const acquire = (props) => {
  const [count, setCount] = useState(0);

  // const handleIncrement = () => setCount((currentCount) => currentCount + 1);

  // const handleDecrement = () => setCount((currentCount) => currentCount - 1);

  // useEffect(() => setCount((currentCount) => currentCount + 1), []);
  let results = props.loading ? <ScrapeLoader /> : null;
  if (props.books.length > 0) {
    results = <SearchResult books={props.books} />;
  }
  return (
    <div>
      <Button
        btnType="primary"
        style={{ float: "left" }}
        clicked={props.togglePages}
      >
        Back To Acquisitions
      </Button>
      <div className="clr" style={{ marginBottom: "0.75rem" }}></div>
      <div className="search-area">
        <input
          type="text"
          placeholder="Enter Title"
          onChange={props.handleSearch}
          value={props.value}
        />
        <Button btnType="fill" clicked={(e) => props.scrapeBooks(e, 20)}>
          Scrape
        </Button>
      </div>
      {results}
    </div>
  );
};
export default acquire;
