import React, { Component } from "react";
import "./acquisition.css";
import Aux from "../../../hoc/Aux";
// import List from "../../../ui/list/list";
import Acquisitions from "./acquisitions";
import Acquire from "./acquire/Acquire";
import { Route } from "react-router-dom";

class Acquisition extends Component {
  state = {
    acquisitions: [],
    books: [],
    searchTerm: "",
    toAcquisitions: true,
    toAcquire: false,
    error: null,
  };

  togglePages = () => {
    console.log("hello");
    // this.setState((prevState) => {
    //   return {
    //     toAcquisitions: !prevState.toAcquisitions,
    //     toAcquire: !prevState.toAcquire,
    //   };
    // });
    this.props.history.push({
      pathname: this.props.match.path + "/acquire",
    });
  };

  render() {
    const attributes = [
      "Date Of Acquisition",
      "Number Of Books Acquired",
      "Budget Cost",
    ];

    let pages = (
      <div>
        <Acquisitions
          attributes={attributes}
          acquisitions={this.state.acquisitions}
          togglePages={this.togglePages}
        />
      </div>
    );

    // if (this.state.toAcquire) {
    //   pages = <Acquire togglePages={this.togglePages} />;
    // }
    return (
      <Aux>
        <Route exact path={this.props.match.path} render={() => pages} />
        <Route
          path={this.props.match.path + "/acquire"}
          render={() => <Acquire togglePages={this.togglePages} />}
        />
      </Aux>
    );
  }
}
export default Acquisition;
