import React, { Component } from "react";
import Aux from "../../../hoc/Aux";
// import List from "../../../ui/list/list";
import Acquisitions from "./acquisitions";
import Acquire from "./acquire";
import Button from "../../../ui/button/button";
import "./acquisition.css";
import { CSSTransition } from "react-transition-group";
class Acquisition extends Component {
  state = {
    acquisitions: [],
    toAcquisitions: true,
    toAcquire: false,
  };

  togglePages = () => {
    console.log("hear");
    this.setState((prevState) => {
      return {
        toAcquisitions: !prevState.toAcquisitions,
        toAcquire: !prevState.toAcquire,
      };
    });
  };

  render() {
    // let acquisitionsClasses = ["acquisitions", "open-acquisitions"];
    // let acquireClasses = ["acquire", "close-acquire"];
    // if (this.state.toAcquire) {
    //   acquisitionsClasses = ["acquisitions", "close-acquisitions"];
    //   acquireClasses = ["acquire", "open-acquire"];
    // }
    const attributes = [
      "Date Of Acquisition",
      "Number Of Books Acquired",
      "Budget Cost",
    ];

    let page = (
      <CSSTransition timeout={300} classNames="slide">
        <Acquisitions
          attributes={attributes}
          acquisitions={this.state.acquisitions}
        />
      </CSSTransition>
    );
    if (this.state.toAcquire) {
      page = (
        <CSSTransition timeout={300} classNames="slide">
          <div>
            <Acquire />
          </div>
        </CSSTransition>
      );
    }
    return (
      <Aux>
        <Button
          btnType="primary"
          style={{ float: "right" }}
          clicked={this.togglePages}
        >
          Go Acquire
        </Button>
        <div className="clr" style={{ marginBottom: "0.75rem" }}></div>

        {page}
      </Aux>
    );
  }
}
export default Acquisition;
