import React, { Component } from "react";
import "./acquisition.css";
import Aux from "../../../hoc/Aux";
// import List from "../../../ui/list/list";
import Acquisitions from "./acquisitions";
import Acquire from "./acquire/acquire";
import axios from "../../../axios-scrape";

class Acquisition extends Component {
  state = {
    acquisitions: [],
    books: [],
    searchTerm: "",
    // toAcquisitions: true,
    toAcquire: false,
    error: null,
    loading: false,
  };
  componentDidMount = () => {
    window.addEventListener("scroll", () => {
      const scrollable =
        document.documentElement.scrollHeight - window.innerHeight;
      const scrolled = window.scrollY;
      if (Math.ceil(scrolled) === scrollable) {
        alert("You v reached the bottom");
      }
    });
    if (sessionStorage.getItem("toAcquire")) {
      console.log("value", sessionStorage.getItem("toAcquire"));
      this.setState({ toAcquire: sessionStorage.getItem("toAcquire") });
    }
  };

  componentDidUnMount = () => {
    window.removeEventListener("scroll");
  };

  toAcquirePage = () => {
    console.log("hello");
    this.setState({ toAcquire: true });

    sessionStorage.setItem("toAcquire", true);
  };

  toAcquisitionsPage = () => {
    this.setState({ toAcquire: false });
    sessionStorage.removeItem("toAcquire");
  };

  scrapeBooks = (startIndex = 0) => {
    this.setState({ books: [] });
    this.setState({ loading: true });
    // setTimeout(5000,()=>{

    // })
    axios
      .get(
        `?q=${this.state.searchTerm}&maxResults=40&startIndex=${+startIndex}`
      )
      .then((res) => {
        console.log(res);
        this.setState({
          books: this.cleanData(res.data.items),
          loading: false,
        });
      })
      .catch((error) => this.setState({ error, loading: false }));
  };

  handleSearch = (e) => {
    this.setState({ searchTerm: e.target.value });
  };

  cleanData = (books) => {
    const cleanedDate = books.map((book) => {
      if (!book.volumeInfo.hasOwnProperty("imageLinks")) {
        book.volumeInfo["imageLinks"] = {
          thumbnail:
            "https://vignette.wikia.nocookie.net/pandorahearts/images/a/ad/Not_available.jpg/revision/latest?cb=20141028171337",
        };
      }
      if (!book.volumeInfo.hasOwnProperty("authors")) {
        book.volumeInfo["authors"] = [];
      }
      return book;
    });
    console.log(cleanedDate);
    return cleanedDate;
  };

  render() {
    const attributes = [
      "Date Of Acquisition",
      "Number Of Books Acquired",
      "Budget Cost",
    ];

    let pages = (
      <Acquisitions
        attributes={attributes}
        acquisitions={this.state.acquisitions}
        togglePages={this.toAcquirePage}
      />
    );

    if (this.state.toAcquire) {
      pages = (
        <Acquire
          togglePages={this.toAcquisitionsPage}
          handleSearch={this.handleSearch}
          scrapeBooks={this.scrapeBooks}
          books={this.state.books}
          value={this.state.value}
          loading={this.state.loading}
        />
      );
    }
    return <Aux>{pages}</Aux>;
  }
}
export default Acquisition;
