import React, { Component } from "react";
import Aux from "../../../hoc/Aux";
// import List from "../../../ui/list/list";
import Acquisitions from "./acquisitions";
import Acquire from "./acquire";
import Button from "../../../ui/button/button";
import "./acquisition.css";
class Acquisition extends Component {
  state = {
    acquisitions: [],
    toAcquisitions: true,
    toAcquire: false,
  };

  togglePages = () => {
    console.log("hear");
    this.setState(() => {
      return {
        toAcquisitions: !this.state.toAcquisitions,
        toAcquire: !this.state.toAcquire,
      };
    });
  };

  render() {
    let acquisitionsClasses = ["acquisition", "open-acquisitions"];
    if (this.state.togglePages) {
      acquisitionsClasses = ["acquisition", "close-acquisitions"];
    }
    const attributes = [
      "Date Of Acquisition",
      "Number Of Books Acquired",
      "Budget Cost",
    ];
    return (
      <Aux>
        <Button
          btnType="primary"
          style={{ float: "right" }}
          clicked={this.togglePages}
        >
          Go Acquire
        </Button>
        <div className="clr" style={{ marginBottom: "0.75rem" }}></div>
        <div className={acquisitionsClasses.join(" ")}>
          <Acquisitions
            attributes={attributes}
            acquisitions={this.state.acquisitions}
          />
        </div>
        <div className="acquire">
          <Acquire />
        </div>
      </Aux>
    );
  }
}
export default Acquisition;
