import React from "react";
import Table from "../../../ui/table/Table";
import { Line } from "react-chartjs-2";
import Aux from "../../../hoc/Aux";
import Button from "../../../ui/button/button";
import styles from "./Acquisition.module.css";

const Acquisitions = (props) => {
  const data = {
    labels: [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "May",
      "Jun",
      "Jul",
      "Aug",
      "Sept",
      "Oct",
      "Nov",
      "Dec",
    ],
    datasets: [
      {
        label: "Year 2020",
        data: [22, 19, 27, 23, 22, 24, 17, 25, 23, 24, 20, 19],
        fill: false, // Don't fill area under the line
        borderColor: "#6c0ba9", // Line color
      },
    ],
  };

  const option = {
    maintainAspectRatio: false,
    legend: {
      // display: true,
      // position: "right",
    },
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  };

  return (
    <Aux>
      <Button
        btnType="primary"
        style={{ float: "right" }}
        clicked={props.togglePages}
      >
        Go Acquire
      </Button>
      <div className="clr" style={{ marginBottom: "0.75rem" }}></div>
      <div className={styles.line - chart}>
        <Line data={data} options={option} style={{ width: "100%" }} />
      </div>
      <div className={style.acquired}>
        <Table
          headers={props.attributes}
          title="Acquisitions"
          data={props.acquisitions}
        />
      </div>
    </Aux>
  );
};
export default Acquisitions;
